SELECT o.request_id, p.product_name, o.product_code, o.request_qty, o.received_qty, o.defective_qty, e.emp_id, e.emp_name, o.create_date FROM order_request o INNER JOIN products p ON o.product_code = p.product_code INNER JOIN employees e ON o.emp_id = e.emp_id
AND p.product_name LIKE '%' || #{query} || '%'
AND o.request_id LIKE '%' || #{request_id} || '%'
AND o.create_date >= #{order_date_start}
AND o.create_date <= #{order_date_end}
AND e.emp_name LIKE '%' || #{emp_name} || '%'
